<script lang="ts">
import { Component, Vue } from "vue-property-decorator";
import anime from "animejs";

@Component({
  name: "sunstate-component-ui-logotype"
})
export default class logotype extends Vue {
  async mounted(): Promise<void> {
    try {
      anime({
        targets: this.$refs["overlay"],
        translateX: "400px",
        duration: 3000,
        easing: "easeInQuart"
      });

      const path = anime.path(this.$refs["ring"] as SVGElement);

      anime({
        targets: this.$refs["light"],
        translateX: path("x"),
        translateY: path("y"),
        opacity: [0, 1, 0],
        easing: "easeInQuart",
        duration: 3000,
        delay: 5000,
        loop: true
      });
    } catch (error) {
      throw error;
    }
  }
}
</script>

